package com.kalynx.simplyabinaryserializer.testutil;

/**
 * Common test data classes used across serializer and deserializer tests.
 * All classes are public with public fields to allow generated bytecode access.
 */
public class TestDataClasses {

    // Individual primitive type test classes

    public static class ByteObject {
        public byte value;
        public ByteObject() {}
        public ByteObject(byte value) { this.value = value; }
    }

    public static class ShortObject {
        public short value;
        public ShortObject() {}
        public ShortObject(short value) { this.value = value; }
    }

    public static class IntObject {
        public int value;
        public IntObject() {}
        public IntObject(int value) { this.value = value; }
    }

    public static class LongObject {
        public long value;
        public LongObject() {}
        public LongObject(long value) { this.value = value; }
    }

    public static class FloatObject {
        public float value;
        public FloatObject() {}
        public FloatObject(float value) { this.value = value; }
    }

    public static class DoubleObject {
        public double value;
        public DoubleObject() {}
        public DoubleObject(double value) { this.value = value; }
    }

    public static class BooleanObject {
        public boolean value;
        public BooleanObject() {}
        public BooleanObject(boolean value) { this.value = value; }
    }

    public static class CharObject {
        public char value;
        public CharObject() {}
        public CharObject(char value) { this.value = value; }
    }

    // Composite test classes

    public static class AllPrimitivesObject {
        public byte byteVal;
        public short shortVal;
        public int intVal;
        public long longVal;
        public float floatVal;
        public double doubleVal;
        public boolean boolVal;
        public char charVal;

        public AllPrimitivesObject() {}

        public AllPrimitivesObject(byte b, short s, int i, long l, float f, double d, boolean bool, char c) {
            this.byteVal = b;
            this.shortVal = s;
            this.intVal = i;
            this.longVal = l;
            this.floatVal = f;
            this.doubleVal = d;
            this.boolVal = bool;
            this.charVal = c;
        }
    }

    public static class SingleIntObject {
        public int value;
        public SingleIntObject() {}
        public SingleIntObject(int value) { this.value = value; }
    }

    public static class MultipleIntsObject {
        public int first;
        public int second;
        public int third;

        public MultipleIntsObject() {}

        public MultipleIntsObject(int first, int second, int third) {
            this.first = first;
            this.second = second;
            this.third = third;
        }
    }

    public static class LongValuesObject {
        public long min;
        public long max;
        public long zero;

        public LongValuesObject() {}

        public LongValuesObject(long min, long max, long zero) {
            this.min = min;
            this.max = max;
            this.zero = zero;
        }
    }

    public static class FloatValuesObject {
        public float floatValue;
        public double doubleValue;

        public FloatValuesObject() {}

        public FloatValuesObject(float floatValue, double doubleValue) {
            this.floatValue = floatValue;
            this.doubleValue = doubleValue;
        }
    }

    public static class BooleanValuesObject {
        public boolean trueValue;
        public boolean falseValue;

        public BooleanValuesObject() {}

        public BooleanValuesObject(boolean trueValue, boolean falseValue) {
            this.trueValue = trueValue;
            this.falseValue = falseValue;
        }
    }

    // Test class with mixed field types (for validation testing)
    public static class MixedFieldsObject {
        public int primitiveField;
        public String nonPrimitiveField;

        public MixedFieldsObject() {}

        public MixedFieldsObject(int primitiveField, String nonPrimitiveField) {
            this.primitiveField = primitiveField;
            this.nonPrimitiveField = nonPrimitiveField;
        }
    }

    // Prevent instantiation
    private TestDataClasses() {}
}

